import React, { useEffect } from 'react';
import { useDispatch, useSelector } from 'react-redux';
import { Link } from 'react-router-dom';
import { addToCart, removeFromCart } from '../actions/cartTableActions';
import MessageBox from '../components/MessageBox';
import hinh1 from '../scss/images/hinh1.jpg'
import '../scss/bookTable.css'

export default function CartScreen(props) {
  const tableId = props.match.params.id;
  const qty = props.location.search
    ? Number(props.location.search.split('=')[1])
    : 1;
  const cart = useSelector((state) => state.cartTable);
  const { cartItems, error } = cart;
  const dispatch = useDispatch();
  useEffect(() => {
    if (tableId) {
      dispatch(addToCart(tableId, qty));
    }
  }, [dispatch, tableId, qty]);

  const removeFromCartHandler = (id) => {
    // delete action
    dispatch(removeFromCart(id));
  };

  const checkoutHandler = () => {
    props.history.push('/signin?redirect=bookingTable');
  };
  return (
    <div className=" row top h-100" 
    // style ={{ backgroundColor : 'white', paddingBottom:'100px', textAlign:'center'}}
    >
      <div className="col-8 table-content">
        <div>
          <h1>Booking Table Cart</h1>
        </div>
        <div className ='table-tab'>
        {error && <MessageBox variant="danger">{error}</MessageBox>}
        {cartItems.length === 0 ? (
          <MessageBox>
            <p className = ' '
          >
            Table is empty.</p> <Link to="/" className = "w-100" 

          >Book a table now</Link>
          <img src ={hinh1} className ='w-50'/>
          </MessageBox>
        ) : (
          <ul>
            {cartItems.map((item) => (
              <li key={item.table}>
                <div className="row">
                  <div>
                    <img
                      src={item.image}
                      alt={item.name}
                      className="small"
                    ></img>
                  </div>
                  <div className="min-30">
                    <Link to={`/table/${item.table}`}>{item.name}</Link>
                  </div>
                  <div>
                    <select
                      value={item.qty}
                      onChange={(e) =>
                        dispatch(
                          addToCart(item.table, Number(e.target.value))
                        )
                      }
                    >
                      {[...Array(item.countInStock).keys()].map((x) => (
                        <option key={x + 1} value={x + 1}>
                          {x + 1}
                        </option>
                      ))}
                    </select>
                  </div>
                  <div>${item.price}</div>
                  <div>
                    <button
                      type="button"
                      onClick={() => removeFromCartHandler(item.table)}
                    >
                      Delete
                    </button>
                  </div>
                </div>
              </li>
            ))}
          </ul>
        )}
        </div>

      </div>
      <div className="col-1">
        <div className="card card-body">
          <ul>
            <li>
              <h2>
                Subtotal ({cartItems.reduce((a, c) => a + c.qty, 0)} items) : $
                {cartItems.reduce((a, c) => a + c.price * c.qty, 0)}
              </h2>
            </li>
            <li>
              <button
                type="button"
                onClick={checkoutHandler}
                className="primary block"
                disabled={cartItems.length === 0}
              >
                Proceed to Checkout
              </button>
            </li>
          </ul>
        </div>
      </div>
    </div>
  );
}
